import React, {Component} from 'react';
import './../App.css';

class MyComponent extends Component{
    constructor(props){
        super(props);
        this.state = {
            isLoaded:false,
            posts: null,
        };
    }
    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/posts')
            .then(res=>res.json())
            .then(posts=>{this.setState({isLoaded:true,posts})});
    }
    render(){
        const {posts, isLoaded} = this.state;
        console.log('**********');
        console.log(posts)
        console.log('**********');
        const tablestyle = {
            border:'1px solid black',
        };
        // return <div></div>;
        return !isLoaded ? (<h6>IS LOADING...</h6>):
         (
            <div>
                <table style={tablestyle}>
                    <th>title</th><th>body</th>
                    <tr>
                        {posts.map((post, i)=><li key={i}><ul>
                        <li>{post.title}</li><li>{post.body}</li>
                        </ul></li>)}
                    </tr>
                </table>
            </div>
        );
    }
}
const TableRow = (props)=> {
    return <tr>
      <td>{props.pos.id}</td>
      <td>{props.user.name}</td>
      <td>{props.user.email}</td>
      <td>`{props.user.address.street} {props.user.address.suit} {props.user.address.city} {props.user.address.zipcode}` </td>
    </tr>;
  }

export default MyComponent;
