import React , {Component} from 'react';
import './Current.css';
export default class Current extends Component {
    constructor(props){
        super(props);
        this.state = {
            city:'',
            state:''
        }
    }
    componentDidMount(){
        const [lat, long] = [this.props.weatherinfo.latitude, this.props.weatherinfo.longitude];
        const url = `https://api.opencagedata.com/geocode/v1/json?q=${lat}+${long}&key=9c325aa45a2c4f81a72b27674accf9c7`
        fetch(url)
            .then(res=>res.json())
            .then(data=>{
                this.setState({city: data.results[0].components.city,
                     state: data.results[0].components.state})
            })

    }
    render(){
        return (
        <div className='userpart current' style={{borderRadius:'1em'}}>
            <p id='current-degree'>{this.props.weatherinfo.currently.apparentTemperature}&#176;F</p>
        </div>
        );
        }
  }
