import React , {Component} from 'react';
import ReactDom from 'react-dom';
import logo from './logo.svg';
import './App.css';

export class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      street:'Shoup Ave',
      city:'Los Angeles',
      state:'',
    }
    this.updateStreet = this.updateStreet.bind(this);
    this.updateCity = this.updateCity.bind.bind(this);
  }

  updateStreet(e){
    e.preventDefault();
    this.setState({street:e.target.value});
  }
  updateCity(e){
    e.preventDefault();
    this.setState({city:e.target.value});
  }
  render(){
    return (
      <div className="App">
        <form className='weatherform'>
          <h1>Weather Search</h1>
          <div id='street'>
          <label className='label'>street</label>
          <input onChange={this.updateStreet} value={this.state.street}></input>
          </div>
          <br></br>
          <div id='city'>
          <label className='label'>city</label>
          <input onChange={this.updateCity} value={this.state.city}></input>
          </div>
        </form>
        <div>
          <h1>Test</h1>
          <h5>street value is:</h5>{this.state.street}
          <h5>city value is:</h5>{this.state.city}
        </div>
      </div>
    );
  }
}

export default App;
