import React , {Component} from 'react';
import ReactDom from 'react-dom';
import logo from './logo.svg';
import './App.css';

export class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      street:'Shoup Ave',
      city:'Los Angeles',
      lat:'',
      long:'',
      states:['Alabama','Alaska','American Samoa','Arizona','Arkansas',
      'California','Colorado','Connecticut','Delaware','District of Columbia',
      'Federated States of Micronesia','Florida','Georgia','Guam','Hawaii',
      'Idaho','Illinois','Indiana','Iowa','Kansas','Kentucky','Louisiana','Maine',
      'Marshall Islands','Maryland','Massachusetts','Michigan','Minnesota',
      'Mississippi','Missouri','Montana','Nebraska','Nevada','New Hampshire',
      'New Jersey','New Mexico','New York','North Carolina','North Dakota',
      'Northern Mariana Islands','Ohio','Oklahoma','Oregon','Palau','Pennsylvania',
      'Puerto Rico','Rhode Island','South Carolina','South Dakota','Tennessee','Texas',
      'Utah','Vermont','Virgin Island','Virginia','Washington','West Virginia','Wisconsin',
      'Wyoming'],
      currState : 'Select',
      currLoc: false,
      users:[],
      loadingcustomers: true,
      customers : []
    }
    this.updateStreet = this.updateStreet.bind(this);
    this.updateCity = this.updateCity.bind(this);
    this.updateStates = this.updateStates.bind(this);
    this.updateCurrloc = this.updateCurrloc.bind(this);
    this.updateClear = this.updateClear.bind(this);
    this.submitform = this.submitform.bind(this);
  }

  updateStreet(e){
    e.preventDefault();
    this.setState({street:e.target.value});
  }
  updateCity(e){
    e.preventDefault();
    this.setState({city:e.target.value});
  }
  updateStates(e){
    e.preventDefault();
    this.setState({currState:e.target.value});
  }
  updateCurrloc(e){
    e.preventDefault();
    this.setState({currLoc:!this.state.currLoc});
  }
  updateClear(e){
    e.preventDefault();
    this.setState({
      street:'Shoup Ave',
      city:'Los Angeles',
      currState : 'Select',
      currLoc: false,
  })
  }
  componentDidMount(){
    fetch('http://jsonplaceholder.typicode.com/users')
      .then(res=>res.json())
      .then((data)=>{
        this.setState({users:data})
      })
      .catch(console.log)
  }
  submitform(e){
    console.log('kos');
    e.preventDefault();
    fetch('http://localhost:5000/api/customers')
      .then(res=>res.json())
      .then((data)=>{
        this.setState({
          customers:data, 
          loadingcustomers:false
        })
        console.log(data);
      })
      console.log('kir');

  }
  render(){
    let testStyle = {
      'margin-top':'50px',
    }
    const rendercustomer = this.state.loadingcustomers;
    return (
      <div className="App">
        <form className='weatherform'>
          <h1>Weather Search</h1>
          <div id='street' className='inputpart'>
          <label className='inputlabel'>street</label>
          <input onChange={this.updateStreet} value={this.state.street}></input>
          </div>
          <div id='city' className='inputpart'>
          <label className='inputlabel'>city</label>
          <input onChange={this.updateCity} value={this.state.city}></input>
          </div>
          <div className='inputpart'>
          <label className='inputlabel'>state</label>
          <select className='state-select' onChange={this.updateStates} value={this.state.currState}>
            {this.state.states.map(st=><option value={st}>{st}</option>)}
          </select>
          </div>
          <div className='inputpart'>
          <label className='current-Location'>
          <input name='currentLocation' type='checkbox' onChange={this.state.updateCurrloc} 
          value={this.state.currLoc}>
          </input>
          current-Location
          </label>
          </div>
          <div className='inputpart'>
          <label className='current-Location'>
          <input name='currentLocation' type='checkbox' onChange={this.state.updateCurrloc} 
          value={this.state.currLoc}>
          </input>
          current-Location
          </label>
          </div>
          <div className='inputpart'>
          <label className='current-Location'>
          <input name='currentLocation' type='checkbox' onChange={this.state.updateCurrloc} 
          value={this.state.currLoc}>
          </input>
          current-Location
          </label>
          </div>
          <input type='submit' id='submit' value='Submit' onClick={this.submitform} />
          <input type='submit' id ='clear' value='clear' onClick={this.updateClear}/>
        </form>
        <div className='userspart'>
          <table>
            <tbody>
              {this.state.users.map(user=><TableRow user={user}></TableRow>)}
            </tbody>
          </table>
        </div>
        <div className='userspart' id='customers'>
        {rendercustomer ? <h3>is loading...</h3> :
        <table>
        <tbody>
          {this.state.customers.map(cust=><CustBlock user={cust}></CustBlock>)}
        </tbody>
        </table> 
        }
        </div>
        <div>
          <h1 style={testStyle}>Test</h1>
          <h5>street value is:</h5>{this.state.street}
          <h5>city value is:</h5>{this.state.city}
          <h5>currState value is:</h5>{this.state.currState}
        </div>

      </div>
    );
  }
}

const TableRow = (props)=> {
  return <tr>
    <td>{props.user.id}</td>
    <td>{props.user.name}</td>
    <td>{props.user.email}</td>
    <td>`{props.user.address.street} {props.user.address.suit} {props.user.address.city} {props.user.address.zipcode}` </td>
  </tr>;
}

const CustBlock = (props) => {
  return <tr>
    <td>{props.user.id}</td>
    <td>{props.user.firstName}</td>
    <td>{props.user.lastName}</td>
  </tr>;
}

export default App;
