import React, {useState, useEffect, useRef} from 'react';
import {Chart} from 'chart.js';

function Hourly(props){
    const reftocanvas = useRef(null);
    useEffect(()=>{
        const can = reftocanvas.current;
        console.log(can)
        const ctx = can.getContext('2d');
        const tempdata = [];
        const labels = []
        // h in props.weatherinfo.hourly.data
        for (let i=0;i<10;i++){tempdata.push(12);labels.push('')}
        const mychart = new Chart(ctx, {
            type: 'bar',
            data: {
                labels,
                datasets: [{
                    label: '# of Votes',
                    data: tempdata,
                    backgroundColor: [
                        'rgba(255, 99, 132, 0.2)',
                        'rgba(54, 162, 235, 0.2)',
                        'rgba(255, 206, 86, 0.2)',
                        'rgba(75, 192, 192, 0.2)',
                        'rgba(153, 102, 255, 0.2)',
                        'rgba(255, 159, 64, 0.2)'
                    ],
                    borderColor: [
                        'rgba(255, 99, 132, 1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)',
                        'rgba(75, 192, 192, 1)',
                        'rgba(153, 102, 255, 1)',
                        'rgba(255, 159, 64, 1)'
                    ],
                    borderWidth: 1
                }]
            },
            options: {
                scales: {
                    yAxes: [{
                        ticks: {
                            beginAtZero: true
                        }
                    }]
                }
            }
        });
    });
    return(
        <div>
        <canvas id="myChart" width="400" height="400" aria-label="Hello ARIA World" ref={reftocanvas}><p>Hello Fallback World</p></canvas>
        </div>
    );
}

export default Hourly
